graphql.query:
query{
  findAllPets{
    cod
    nome
    dono {
      nome
      email
    }
  }
  
  findPetById(cod:18){
    nome
    dono {
      nome
      email
      bairro
    }
  }
  
  findAllPeople {
    cod
    nome
  }
  
  findPeopleById(cod:3){
    nome
    email
    bairro
    genero
  }
}
